export function update() {
    if (this.isAttacking) {
        // Evita outras ações durante o ataque
        return;
    }

    // Verificar se uma tecla de ataque está pressionada
    if (wasd.arrowUp.isDown || wasd.arrowLeft.isDown || wasd.arrowDown.isDown || wasd.arrowRight.isDown) {
        this.isAttacking = true;

        // Determinar a direção do ataque
        let attackAnim, attackAnimArma, attackAnimBlusa, attackAnimChapeu, attackAnimPerna, attackAnimPes;
        if (wasd.arrowUp.isDown) {
            attackAnim = 'attack_up';
            attackAnimArma = 'attack_up_arma';
            attackAnimBlusa = 'attack_up_atkblusa';
            attackAnimChapeu = 'attack_up_atkchapeu';
            attackAnimPerna = 'attack_up_atkperna';
            attackAnimPes = 'attack_up_atkpes';
        } else if (wasd.arrowLeft.isDown) {
            attackAnim = 'attack_left';
            attackAnimArma = 'attack_left_arma';
            attackAnimBlusa = 'attack_left_atkblusa';
            attackAnimChapeu = 'attack_left_atkchapeu';
            attackAnimPerna = 'attack_left_atkperna';
            attackAnimPes = 'attack_left_atkpes';
        } else if (wasd.arrowDown.isDown) {
            attackAnim = 'attack_down';
            attackAnimArma = 'attack_down_arma';
            attackAnimBlusa = 'attack_down_atkblusa';
            attackAnimChapeu = 'attack_down_atkchapeu';
            attackAnimPerna = 'attack_down_atkperna';
            attackAnimPes = 'attack_down_atkpes';
        } else if (wasd.arrowRight.isDown) {
            attackAnim = 'attack_right';
            attackAnimArma = 'attack_right_arma';
            attackAnimBlusa = 'attack_right_atkblusa';
            attackAnimChapeu = 'attack_right_atkchapeu';
            attackAnimPerna = 'attack_right_atkperna';
            attackAnimPes = 'attack_right_atkpes';
        }

        // Tocar as animações de ataque
        player.anims.play(attackAnim, true);
        arma.anims.play(attackAnimArma, true);
        atkblusa.anims.play(attackAnimBlusa, true);
        atkchapeu.anims.play(attackAnimChapeu, true);
        atkperna.anims.play(attackAnimPerna, true);
        atkpes.anims.play(attackAnimPes, true);

        // Tornar visíveis apenas os sprites de ataque
        arma.setVisible(true);
        atkblusa.setVisible(true);
        atkchapeu.setVisible(true);
        atkperna.setVisible(true);
        atkpes.setVisible(true);
        blusa.setVisible(false);
        chapeu.setVisible(false);
        pernas.setVisible(false);
        pes.setVisible(false);

        // Desativar movimento durante o ataque
        playerContainer.body.setVelocityX(0);
        playerContainer.body.setVelocityY(0);

        // Impedir outras animações durante o ataque
        this.time.delayedCall(1000, () => { // A duração do ataque pode ser ajustada conforme necessário
            this.isAttacking = false;
            arma.setVisible(false); // Torna a arma invisível após o ataque
            atkblusa.setVisible(false);
            atkchapeu.setVisible(false);
            atkperna.setVisible(false);
            atkpes.setVisible(false);
            blusa.setVisible(true);
            chapeu.setVisible(true);
            pernas.setVisible(true);
            pes.setVisible(true);

            if (this.parado == 'frente') {
                player.anims.play('turn');
            } else {
                player.anims.play('turn2');
            }
        }, [], this);
    } else {
        // Movimentação normal do jogador
        if (wasd.left.isDown) {
            playerContainer.body.setVelocityX(-160);
            player.anims.play('left', true);
            chapeu.anims.play('left_chapeu', true);
            blusa.anims.play('left_blusa', true);
            pernas.anims.play('left_pernas', true);
            pes.anims.play('left_pes', true);
        } else if (wasd.right.isDown) {
            playerContainer.body.setVelocityX(160);
            player.anims.play('right', true);
            chapeu.anims.play('right_chapeu', true);
            blusa.anims.play('right_blusa', true);
            pernas.anims.play('right_pernas', true);
            pes.anims.play('right_pes', true);
        } else {
            playerContainer.body.setVelocityX(0);
        }

        if (wasd.up.isDown) {
            playerContainer.body.setVelocityY(-160);
            player.anims.play('up', true);
            chapeu.anims.play('up_chapeu', true);
            blusa.anims.play('up_blusa', true);
            pernas.anims.play('up_pernas', true);
            pes.anims.play('up_pes', true);
            this.parado = 'costa';
        } else if (wasd.down.isDown) {
            playerContainer.body.setVelocityY(160);
            player.anims.play('down', true);
            chapeu.anims.play('down_chapeu', true);
            blusa.anims.play('down_blusa', true);
            pernas.anims.play('down_pernas', true);
            pes.anims.play('down_pes', true);
            this.parado = 'frente';
        } else {
            playerContainer.body.setVelocityY(0);
        }

        if (playerContainer.body.velocity.x === 0 && playerContainer.body.velocity.y === 0) {
            if (this.parado == 'frente') {
                player.anims.play('turn');
                chapeu.anims.play('turn_chapeu');
                blusa.anims.play('turn_blusa');
                pernas.anims.play('turn_pernas');
                pes.anims.play('turn_pes');
            } else {
                player.anims.play('turn2');
                chapeu.anims.play('turn2_chapeu');
                blusa.anims.play('turn2_blusa');
                pernas.anims.play('turn2_pernas');
                pes.anims.play('turn2_pes');
            }
        }
    }
}
